-----------------------------------------------------------------------------------------
-- backfill asn.all.groups permissions into existing sites and groups
-----------------------------------------------------------------------------------------
CREATE TABLE PERMISSIONS_SRC_TEMP (ROLE_NAME VARCHAR(99), FUNCTION_NAME VARCHAR(99));
CREATE TABLE PERMISSIONS_TEMP (ROLE_KEY INTEGER, FUNCTION_KEY INTEGER);

-- Backfill sites
-- updated with CLE roles

-- Lookup the role and function keys
select SRR.ROLE_KEY, SRF.FUNCTION_KEY
from PERMISSIONS_SRC_TEMP TMPSRC
join SAKAI_REALM_ROLE SRR on (TMPSRC.ROLE_NAME = SRR.ROLE_NAME)
join SAKAI_REALM_FUNCTION SRF on (TMPSRC.FUNCTION_NAME = SRF.FUNCTION_NAME);

-- Insert the new functions into the roles of any existing realm that has the role (don't convert the "!site.helper" or any group realms)
select
    SRRFD.REALM_KEY, SRRFD.ROLE_KEY, TMP.FUNCTION_KEY
from
    (select distinct SRRF.REALM_KEY, SRRF.ROLE_KEY from SAKAI_REALM_RL_FN SRRF) SRRFD
	 join PERMISSIONS_TEMP TMP on (SRRFD.ROLE_KEY = TMP.ROLE_KEY)
	 join SAKAI_REALM SR on (SRRFD.REALM_KEY = SR.REALM_KEY)
	 where SR.REALM_ID not like '!%'
	 and SR.REALM_ID like '/site/%'
	 and SR.REALM_ID not like '/site/~%'
	 and SR.REALM_ID not like '/site/!%'
	 and SR.REALM_ID not like '/site/%/group/%'
	 and not exists (
	     select 1
            from SAKAI_REALM_RL_FN SRRFI
	         where SRRFI.REALM_KEY=SRRFD.REALM_KEY and SRRFI.ROLE_KEY=SRRFD.ROLE_KEY and  SRRFI.FUNCTION_KEY=TMP.FUNCTION_KEY
	 );



-- Insert the new functions into the roles of any existing group realm that has the role (don't convert the "!site.helper")
select
    SRRFD.REALM_KEY, SRRFD.ROLE_KEY, TMP.FUNCTION_KEY
from
    (select distinct SRRF.REALM_KEY, SRRF.ROLE_KEY from SAKAI_REALM_RL_FN SRRF) SRRFD
	 join PERMISSIONS_TEMP TMP on (SRRFD.ROLE_KEY = TMP.ROLE_KEY)
	 join SAKAI_REALM SR on (SRRFD.REALM_KEY = SR.REALM_KEY)
	 where SR.REALM_ID not like '!%'
	 and SR.REALM_ID not like '/site/%'
	 and SR.REALM_ID not like '/site/~%'
	 and SR.REALM_ID not like '/site/!%'
	 and SR.REALM_ID like '/site/%/group/%'
	 and not exists (
	     select 1
            from SAKAI_REALM_RL_FN SRRFI
	         where SRRFI.REALM_KEY=SRRFD.REALM_KEY and SRRFI.ROLE_KEY=SRRFD.ROLE_KEY and  SRRFI.FUNCTION_KEY=TMP.FUNCTION_KEY
	 );

-- clear out, prepare for next set of perms to backfill
